/* backward function of region 1 */
/* based on Eq.(11) of IAPWS-IF97*/
"use strict"
export function Tph1(SP){
  var i;
  var eta;
  var Hn;
  var T;  
  var II = [];
  var JJ = [];
  var an = [];
  
  Hn= 2500.0;
  II[ 1]=  0  ;  JJ[ 1]=   0  ;  an[ 1]=  -0.23872489924521e+3  ;
  II[ 2]=  0  ;  JJ[ 2]=   1  ;  an[ 2]=   0.40421188637945e+3  ;
  II[ 3]=  0  ;  JJ[ 3]=   2  ;  an[ 3]=   0.11349746881718e+3  ;
  II[ 4]=  0  ;  JJ[ 4]=   6  ;  an[ 4]=  -0.58457616048039e+1  ;
  II[ 5]=  0  ;  JJ[ 5]=  22  ;  an[ 5]=  -0.15285482413140e-3  ;
  II[ 6]=  0  ;  JJ[ 6]=  32  ;  an[ 6]=  -0.10866707695377e-5  ;
  II[ 7]=  1  ;  JJ[ 7]=   0  ;  an[ 7]=  -0.13391744872602e+2  ;
  II[ 8]=  1  ;  JJ[ 8]=   1  ;  an[ 8]=   0.43211039183559e+2  ;
  II[ 9]=  1  ;  JJ[ 9]=   2  ;  an[ 9]=  -0.54010067170506e+2  ;
  II[10]=  1  ;  JJ[10]=   3  ;  an[10]=   0.30535892203916e+2  ;
  II[11]=  1  ;  JJ[11]=   4  ;  an[11]=  -0.65964749423638e+1  ;
  II[12]=  1  ;  JJ[12]=  10  ;  an[12]=   0.93965400878363e-2  ;
  II[13]=  1  ;  JJ[13]=  32  ;  an[13]=   0.11573647505340e-6  ;
  II[14]=  2  ;  JJ[14]=  10  ;  an[14]=  -0.25858641282073e-4  ;
  II[15]=  2  ;  JJ[15]=  32  ;  an[15]=  -0.40644363084799e-8  ;
  II[16]=  3  ;  JJ[16]=  10  ;  an[16]=   0.66456186191635e-7  ;
  II[17]=  3  ;  JJ[17]=  32  ;  an[17]=   0.80670734103027e-10  ;
  II[18]=  4  ;  JJ[18]=  32  ;  an[18]=  -0.93477771213947e-12  ;
  II[19]=  5  ;  JJ[19]=  32  ;  an[19]=   0.58265442020601e-14  ;
  II[20]=  6  ;  JJ[20]=  32  ;  an[20]=  -0.15020185953503e-16  ;

  eta = SP.h / Hn;
  T = 0.0;
  for(i=1;i<=20;i++){
    T = T + an[i]*Math.pow(SP.P,II[i])*Math.pow(eta+1.0,JJ[i]);
  }
  SP.T=T;
  
  return 1;
}

export function Tps1(SP){
  var i;
  var T;  
  var II = [];
  var JJ = [];
  var an = [];
  

  II[ 1]=  0  ;  JJ[ 1]=   0  ;  an[ 1]=   0.17478268058307e+3  ;
  II[ 2]=  0  ;  JJ[ 2]=   1  ;  an[ 2]=   0.34806930892873e+2  ;
  II[ 3]=  0  ;  JJ[ 3]=   2  ;  an[ 3]=   0.65292584978455e+1  ;
  II[ 4]=  0  ;  JJ[ 4]=   3  ;  an[ 4]=   0.33039981775489e+0  ;
  II[ 5]=  0  ;  JJ[ 5]=  11  ;  an[ 5]=  -0.19281382923196e-6  ;
  II[ 6]=  0  ;  JJ[ 6]=  31  ;  an[ 6]=  -0.24909197244573e-22  ;
  II[ 7]=  1  ;  JJ[ 7]=   0  ;  an[ 7]=  -0.26107636489332e+0  ;
  II[ 8]=  1  ;  JJ[ 8]=   1  ;  an[ 8]=   0.22592965981586e+0  ;
  II[ 9]=  1  ;  JJ[ 9]=   2  ;  an[ 9]=  -0.64256463395226e-1  ;
  II[10]=  1  ;  JJ[10]=   3  ;  an[10]=   0.78876289270526e-2  ;
  II[11]=  1  ;  JJ[11]=  12  ;  an[11]=   0.35672110607366e-9  ;
  II[12]=  1  ;  JJ[12]=  31  ;  an[12]=   0.17332496994895e-23  ;
  II[13]=  2  ;  JJ[13]=   0  ;  an[13]=   0.56608900654837e-3  ;
  II[14]=  2  ;  JJ[14]=   1  ;  an[14]=  -0.32635483139717e-3  ;
  II[15]=  2  ;  JJ[15]=   2  ;  an[15]=   0.44778286690632e-4  ;
  II[16]=  2  ;  JJ[16]=   9  ;  an[16]=  -0.51322156908507e-9  ;
  II[17]=  2  ;  JJ[17]=  31  ;  an[17]=  -0.42522657042207e-25  ;
  II[18]=  3  ;  JJ[18]=  10  ;  an[18]=   0.26400441360689e-12  ;
  II[19]=  3  ;  JJ[19]=  32  ;  an[19]=   0.78124600459723e-28  ;
  II[20]=  4  ;  JJ[20]=  32  ;  an[20]=  -0.30732199903668e-30  ;

  T = 0.0;
  for(i=1;i<=20;i++){
          T = T + an[i]*Math.pow(SP.P,II[i])*Math.pow(SP.s+2.0,JJ[i]);
  }
  SP.T=T;
  
  return 1;
}
    
